import React, {useEffect, useState } from 'react'
import { GifGridItem } from './GifGridItem';

export const GifGrid = ({ category }) => {
 
    const [images, setImages] = useState([]);

    useEffect(()=> {
        getGifs();
    }, []) // renderizado por una vez mediante lista de dependencias

    const getGifs = async() => {

        const url = 'https://api.giphy.com/v1/gifs/search?q=Rick+and+Morty&limit=10&api_key=gyaW7LNewwZzbcC4upvErADYRU7fAHMo'
            const resp = await fetch(url);
            const {data} = await resp.json();

            const gifs = data.map(img => {
                return {
                    id: img.id,
                    title: img.title,
                    url: img.images?.downsized_medium.url
                }
            })
            setImages(gifs);
    };

    return (
        <>
            <h3>{category}</h3>
            {
                images.map(img => {
                    return <GifGridItem 
                        key= {img.id}
                        img= {img}
                    />
                })
            } 
        </>
    )
}
